import { useState, useEffect, useCallback } from "react";
import { InputCheckbox } from "../InputCheckbox";
import { TransactionPaneComponent } from "./types";

export const TransactionPane: TransactionPaneComponent = ({
  transaction,
  loading,
  setTransactionApproval: consumerSetTransactionApproval,
}) => {
  const [approved, setApproved] = useState(transaction.approved);

  // Sync local state with the `transaction.approved` prop when it changes
  useEffect(() => {
    setApproved(transaction.approved);
  }, [transaction.approved]);

  // Handle checkbox changes with an async callback
  const handleCheckboxChange = useCallback(
    async (newValue: boolean) => {
      setApproved(newValue); // Update local state optimistically

      try {
        console.log("Updating transaction approval on the server...");
        // Call the global update function
        await consumerSetTransactionApproval({
          transactionId: transaction.id,
          newValue,
        });
        console.log("Update successful.");
      } catch (error) {
        console.error("Failed to update transaction approval:", error);
        // Rollback to previous state if update fails
        setApproved(!newValue);
      }
    },
    [transaction.id, consumerSetTransactionApproval]
  );

  return (
    <div className="RampPane">
      <div className="RampPane--content">
        <p className="RampText">{transaction.merchant}</p>
        <b>{moneyFormatter.format(transaction.amount)}</b>
        <p className="RampText--hushed RampText--s">
          {transaction.employee.firstName} {transaction.employee.lastName} - {transaction.date}
        </p>
      </div>
      <InputCheckbox
        id={transaction.id}
        checked={approved}
        disabled={loading} // Disable while loading to prevent multiple clicks
        onChange={handleCheckboxChange}
      />
    </div>
  );
};

const moneyFormatter = new Intl.NumberFormat("en-US", {
  style: "currency",
  currency: "USD",
});
