import { useState, useEffect } from "react";
import { InputCheckbox } from "../InputCheckbox";
import { TransactionPaneComponent } from "./types";

export const TransactionPane: TransactionPaneComponent = ({
  transaction,
  loading,
  setTransactionApproval: consumerSetTransactionApproval,
}) => {
  const [approved, setApproved] = useState(transaction.approved);

  // bug 2 -- update changes 
  useEffect(() => {
    setApproved(transaction.approved);
  }, [transaction.approved]);


  const handleCheckboxChange = async (newValue: boolean) => {
    setApproved(newValue); // Update local state immediately
    console.log("updating globally ")

    try {
      // Immediately call the global update function
      console.log("updating globally ")
      await consumerSetTransactionApproval({
        transactionId: transaction.id,
        newValue,
      });
    } catch (error) {
      // Rollback if the request fails
      setApproved(!newValue);
    }
  };





  return (
    <div className="RampPane">
      <div className="RampPane--content">
        <p className="RampText">{transaction.merchant}</p>
        <b>{moneyFormatter.format(transaction.amount)}</b>
        <p className="RampText--hushed RampText--s">
          {transaction.employee.firstName} {transaction.employee.lastName} - {transaction.date}
        </p>
      </div>
      <InputCheckbox
        id={transaction.id}
        checked={approved}
        // disabled={loading} 
        onChange={handleCheckboxChange}
      />
    </div>
  );
};

const moneyFormatter = new Intl.NumberFormat("en-US", {
  style: "currency",
  currency: "USD",
});
