import { Fragment, useCallback, useEffect, useMemo, useState } from "react";
import { InputSelect } from "./components/InputSelect";
import { Instructions } from "./components/Instructions";
import { Transactions } from "./components/Transactions";
import { useEmployees } from "./hooks/useEmployees";
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions";
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee";
import { EMPTY_EMPLOYEE } from "./utils/constants";
import { Employee } from "./utils/types";

export function App() {
  const { data: employees, ...employeeUtils } = useEmployees();
  const { data: paginatedTransactions, ...paginatedTransactionsUtils } = usePaginatedTransactions();
  const { data: transactionsByEmployee, ...transactionsByEmployeeUtils } = useTransactionsByEmployee();
  const [isLoading, setIsLoading] = useState(false);
  const [transactions, setTransactions] = useState([]); // Keep track of transactions

  // Load all transactions (including pagination)
  const loadAllTransactions = useCallback(async () => {
    setIsLoading(true);
    transactionsByEmployeeUtils.invalidateData(); // Clear any employee-specific data

    await employeeUtils.fetchAll(); // Fetch employee data
    setIsLoading(false);

    const newPaginatedTransactions = await paginatedTransactionsUtils.fetchAll();
    if (newPaginatedTransactions?.data) {
      // Append new transactions to the existing list instead of replacing
      setTransactions((prevTransactions) => [
        ...prevTransactions,
        ...newPaginatedTransactions.data,
      ]);
    }
  }, [employeeUtils, paginatedTransactionsUtils, transactionsByEmployeeUtils]);

  // Load transactions filtered by employee
  const loadTransactionsByEmployee = useCallback(
    async (employeeId) => {
      paginatedTransactionsUtils.invalidateData(); // Clear paginated transactions data
      setTransactions([]); // Clear transactions when filtering by employee

      await transactionsByEmployeeUtils.fetchById(employeeId);
      if (transactionsByEmployee) {
        setTransactions(transactionsByEmployee); // Set transactions based on employee
      }
    },
    [paginatedTransactionsUtils, transactionsByEmployeeUtils, transactionsByEmployee]
  );

  // Load all transactions initially when employees data is not null
  useEffect(() => {
    if (employees === null && !employeeUtils.loading) {
      loadAllTransactions();
    }
  }, [employeeUtils.loading, employees, loadAllTransactions]);

  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />

        <hr className="RampBreak--l" />

        <InputSelect<Employee>
          isLoading={isLoading}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees === null ? [] : [EMPTY_EMPLOYEE, ...employees]}
          label="Filter by employee"
          loadingLabel="Loading employees"
          parseItem={(item) => ({
            value: item.id,
            label: item === EMPTY_EMPLOYEE ? "All Employees" : `${item.firstName} ${item.lastName}`,
          })}
          onChange={async (newValue) => {
            // bug 3 - check if the id is null or empty employee
            if (newValue === null || newValue.id === EMPTY_EMPLOYEE.id) {
              // bug three --- load if empty
              await loadAllTransactions();
              return;
            }

            await loadTransactionsByEmployee(newValue.id);
          }}
        />

        <div className="RampBreak--l" />

        <div className="RampGrid">
          <Transactions transactions={transactions} />

          {transactions !== null && paginatedTransactions?.nextPage && (
            <button
              className="RampButton"
              disabled={paginatedTransactionsUtils.loading || paginatedTransactions?.nextPage == null}
              onClick={async () => {
                await loadAllTransactions(); // Load more transactions and append
              }}
            >
              View More
            </button>
          )}
        </div>
      </main>
    </Fragment>
  );
}
