import { useState } from "react";
import { fakeFetch } from "../../utils/fetch";
import { InputCheckbox } from "../InputCheckbox";
import { TransactionPaneComponent } from "./types";

export const TransactionPane: TransactionPaneComponent = ({ transaction }) => {
  const [approved, setApproved] = useState(transaction.approved);
  const [isProcessing, setIsProcessing] = useState(false);

  // Handler for when the approval checkbox changes
  const handleApprovalChange = async (newValue) => {
    setApproved(newValue); // Optimistically update UI
    setIsProcessing(true);

    try {
      // Simulate API call to set approval
      await fakeFetch("setTransactionApproval", {
        transactionId: transaction.id,
      });
    } catch (error) {
      console.error("Error updating transaction approval:", error);
      setApproved(!newValue); // Roll back the update if the API call fails
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="RampPane">
      <div className="RampPane--content">
        <p className="RampText">{transaction.merchant}</p>
        <b>{moneyFormatter.format(transaction.amount)}</b>
        <p className="RampText--hushed RampText--s">
          {transaction.employee.firstName} {transaction.employee.lastName} -{" "}
          {transaction.date}
        </p>
      </div>
      <InputCheckbox
        id={transaction.id}
        checked={approved}
        disabled={isProcessing}
        onChange={handleApprovalChange}
      />
    </div>
  );
};

const moneyFormatter = new Intl.NumberFormat("en-US", {
  style: "currency",
  currency: "USD",
});
