import { Fragment, useCallback, useEffect, useState } from "react";
import { InputSelect } from "./components/InputSelect";
import { Instructions } from "./components/Instructions";
import { Transactions } from "./components/Transactions";
import { useEmployees } from "./hooks/useEmployees";
import { usePaginatedTransactions } from "./hooks/usePaginatedTransactions";
import { useTransactionsByEmployee } from "./hooks/useTransactionsByEmployee";
import { EMPTY_EMPLOYEE } from "./utils/constants";
import { Employee } from "./utils/types";

export function App() {
  const { data: employees, ...employeeUtils } = useEmployees();
  const { data: paginatedTransactions, ...paginatedTransactionsUtils } = usePaginatedTransactions();
  const { data: transactionsByEmployee, ...transactionsByEmployeeUtils } = useTransactionsByEmployee();
  const [isLoading, setIsLoading] = useState(false);
  const [allTransactions, setAllTransactions] = useState([]);

  // Load all transactions and append them to the existing list
  const loadAllTransactions = useCallback(async () => {
    setIsLoading(true);
    transactionsByEmployeeUtils.invalidateData(); // Clear employee-specific transactions

    try {
      await employeeUtils.fetchAll(); // Fetch employee list

      const newPaginatedTransactions = await paginatedTransactionsUtils.fetchAll(); // Fetch all transactions
      console.log("Fetched Transactions:", newPaginatedTransactions); // Debug log to verify the fetched data

      if (newPaginatedTransactions?.data) {
        setAllTransactions((prevTransactions) => [
          ...prevTransactions,
          ...newPaginatedTransactions.data,
        ]);
      }
    } catch (error) {
      console.error("Error fetching transactions:", error);
    } finally {
      setIsLoading(false);
    }
  }, [employeeUtils, paginatedTransactionsUtils, transactionsByEmployeeUtils]);

  // Load transactions filtered by employee
  const loadTransactionsByEmployee = useCallback(
    async (employeeId: string) => {
      paginatedTransactionsUtils.invalidateData();
      try {
        const employeeTransactions = await transactionsByEmployeeUtils.fetchById(employeeId);
        console.log("Fetched Employee Transactions:", employeeTransactions); // Debug log to verify the fetched data
        if (employeeTransactions) {
          setAllTransactions(employeeTransactions);
        }
      } catch (error) {
        console.error("Error fetching employee transactions:", error);
      }
    },
    [paginatedTransactionsUtils, transactionsByEmployeeUtils]
  );

  // Initial load of all transactions
  useEffect(() => {
    if (employees === null && !employeeUtils.loading) {
      loadAllTransactions();
    }
  }, [employeeUtils.loading, employees, loadAllTransactions]);

  return (
    <Fragment>
      <main className="MainContainer">
        <Instructions />

        <hr className="RampBreak--l" />

        <InputSelect<Employee>
          isLoading={isLoading}
          defaultValue={EMPTY_EMPLOYEE}
          items={employees === null ? [] : [EMPTY_EMPLOYEE, ...employees]}
          label="Filter by employee"
          loadingLabel="Loading employees"
          parseItem={(item) => ({
            value: item.id,
            label: item === EMPTY_EMPLOYEE ? "All Employees" : `${item.firstName} ${item.lastName}`,
          })}
          onChange={async (newValue) => {
            // Check if the selected value is null or empty employee
            if (newValue === null || newValue.id === EMPTY_EMPLOYEE.id) {
              await loadAllTransactions();
              return;
            }

            await loadTransactionsByEmployee(newValue.id);
          }}
        />

        <div className="RampBreak--l" />

        <div className="RampGrid">
          {isLoading ? (
            <div>Loading transactions...</div>
          ) : (
            <Transactions transactions={allTransactions} />
          )}

          {allTransactions.length > 0 && paginatedTransactions?.nextPage != null && (
            <button
              className="RampButton"
              disabled={paginatedTransactionsUtils.loading}
              onClick={async () => {
                await loadAllTransactions();
              }}
            >
              View More
            </button>
          )}
        </div>
      </main>
    </Fragment>
  );
}
