const loadAllTransactions = useCallback(async () => {
  setIsLoading(true);
  transactionsByEmployeeUtils.invalidateData(); // Reset employee-specific data

  // Fetch paginated transactions and assume it returns an object that contains a data array
  const fetchedTransactions = await paginatedTransactionsUtils.fetchAll();

  // Safely add fetched transactions to allTransactions
  if (fetchedTransactions && fetchedTransactions.hasOwnProperty('data')) {
    const newTransactions = (fetchedTransactions as { data: Transaction[] }).data;

    setAllTransactions((prevTransactions) => [
      ...prevTransactions,
      ...newTransactions,
    ]);
  }

  setIsLoading(false);
}, [paginatedTransactionsUtils, transactionsByEmployeeUtils]);
