import React, { useEffect, useState } from "react";

const Transactions = ({ transactions }) => {
  const [localTransactions, setLocalTransactions] = useState(transactions);
  const [localApprovals, setLocalApprovals] = useState({});

  // Load approvals from localStorage on mount
  useEffect(() => {
    const savedApprovals = JSON.parse(localStorage.getItem("approvals") || "{}");
    setLocalApprovals(savedApprovals);
  }, []);

  // Save approvals to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem("approvals", JSON.stringify(localApprovals));
  }, [localApprovals]);

  const setTransactionApproval = (transactionId, newValue) => {
    setLocalApprovals((prevApprovals) => ({
      ...prevApprovals,
      [transactionId]: newValue,
    }));
  };

  useEffect(() => {
    const updatedTransactions = transactions.map((transaction) => ({
      ...transaction,
      approved: localApprovals[transaction.id] !== undefined
        ? localApprovals[transaction.id]
        : transaction.approved,
    }));

    setLocalTransactions(updatedTransactions);
  }, [transactions, localApprovals]);

  return (
    <div data-testid="transaction-container">
      {localTransactions.map((transaction) => (
        <TransactionPane
          key={transaction.id}
          transaction={transaction}
          setTransactionApproval={setTransactionApproval}
        // other props as needed...
        />
      ))}
    </div>
  );
};
